          const data = await fetch(api + url);
    
          const dataJson = await data.json();
          setShortUrl([dataJson.result.short_link, ...shortUrl]);
        }
        Fetch();
      }
    
    return(<>
    <input
        id="urlInput"
        type="text"
        value={url}
        onChange={(event) => {
          setUrl(event.target.value);
        }}
      ></input>
      <button onClick={handleClick}>Submit</button>
      <ol>
        {shortUrl.map((el) => (
          <li>{el}</li>
